import React, { useState } from 'react';

interface Props {
	postFunction: (post: Post) => void;
}

interface Post {
	title: string;
	content: string;
}

async function apiPostNote(title: string, content: string) {
	let response = await fetch(
		`http://localhost:3000/articles?title=${title}&content=${content}`,
		{
			method: 'POST',
		},
	);

	return response.status;
}

function CreateArea(props: Props) {
	const [post, setPost] = useState<Post>({
		title: '',
		content: '',
	});

	function updatePost(event: any) {
		const { name, value } = event.target;
		setPost((prevVal) => {
			return {
				...prevVal,
				[name]: value,
			};
		});
	}

	function submitForm(event: any) {
		props.postFunction(post);
		apiPostNote(post.title, post.content);
		setPost({
			title: '',
			content: '',
		});
		event.preventDefault();
	}

	return (
		<div>
			<form className="create-note">
				<input
					onChange={updatePost}
					name="title"
					placeholder="Question Summary"
					value={post.title}
				/>
				<textarea
					onChange={updatePost}
					name="content"
					placeholder="Details"
					rows={3}
					value={post.content}
				/>
				<button
					onClick={(e: any) => {
						submitForm(e);
					}}
				>
					Add
				</button>
			</form>
		</div>
	);
}
export default CreateArea;
